import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ViewMovieService } from 'src/app/view-movie.service';


// aqui debo injectar el servicio de movies, para que me jale la peli por ID


@Component({
  selector: 'app-view-movie',
  templateUrl: './view-movie.component.html',
  styleUrls: ['./view-movie.component.css']
})


export class ViewMovieComponent implements OnInit {
  movie: Movie;

  constructor (

    private route: ActivatedRoute,
    private viewMovie: ViewMovieService
  ) { }

  ngOnInit()  {
    this.route.paramMap.subscribe(params => {
      const id = params.get('id'); 
      if (id){
        this.viewMovie.getMovieById(id).subscribe (
          movie => {
            this.movie = movie;
          },
          error => {
            console.error ('error fetching movie:', error);
          }
        )
      }
    });
  }
}
